    cleaned = cleaned.replace(/<em>/gi, '<i>');
    cleaned = cleaned.replace(/<\/em>/gi, '</i>');

    // Remover divs vazias e spans desnecess√°rios
    cleaned = cleaned.replace(/<div><\/div>/gi, '');
    cleaned = cleaned.replace(/<span[^>]*>(.*?)<\/span>/gi, '$1');
    
    // Converter quebras de linha duplas em par√°grafos
